function applyGravity(obj1, obj2, constant) {
	let ax = 0;
	let ay = 0;

	const dx = obj1.x - obj2.x;
	const dy = obj1.y - obj2.y;
	const dsq = dx * dx + dy * dy;
	const dr = Math.sqrt(dsq);

	if (dsq > 5) {
		const force = (constant * ((obj1.mass * obj2.mass) / dsq))
		ax += force * dx / dr;
		ay += force * dy / dr;
	}

	obj1.vx -= ax;
	obj1.vy -= ay;
}

const dx = obj1.x - obj2.x;
const dy = obj1.y - obj2.y;
const dsq = dx * dx + dy * dy;
const dr = Math.sqrt(dsq);

if (dsq > 5) {
	const force = (constant * ((obj1.mass * obj2.mass) / dsq))
	ax += force * dx / dr;
	ay += force * dy / dr;
}
