let channel = 5; // 0 - 7
let write = [1, 8+channel << 4, 0];
let mut read: [u8; 3] = [0; 3];
let mut transfer = SpidevTransfer::read_write(&write, &mut read);

self.cs.set_low();
match self.spi.transfer(&mut transfer) {
	Ok(_) => (),
	Err(_) => return Err(Errors::FailedRead)
};
self.cs.set_high();
